int countLines(const char* str, const string& wordToSearch) {
    int count = 0;
    int i = 0;
    while (i < strlen(str)) {
        if (strchr(str, '\\') != nullptr) {
            i++; // пропускаем символ обратного слеша
        } else if (strchr(str, '\n') != nullptr) {
            count++;
        } else {
            i++;
        }
    }

    for (int j = 0; j < strlen(str); j++) {
